            numberPages: _totalPages,
            onPageChange: (int index) {
              // Update the current page when the user selects a new page
              setState(() {
                _currentPage = index + 1; // Page index starts from 0
              });
            },
            initialPage: _currentPage - 1, // Set the initial page
          ),
        ],
      ),
    );
  }
}
