```dart
import 'package:flutter/material.dart';
import 'package:search_page/search_page.dart';

class RealFlutter extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: Text('Search Page Example'),
          actions: [
