class _FractionCalculatorState extends State<FractionCalculator> {
  // Declare variables to hold user input
  String numerator1 = '';
  String denominator1 = '';
  String numerator2 = '';
  String denominator2 = '';
  String result = '';

  // Function to calculate the sum of two fractions
