  @override
  Widget build(BuildContext context) {
    return Column(
      mainAxisAlignment: MainAxisAlignment.center,
      children: [
        Text('Config Directory: $configDir'),
        Text('Data Directory: $dataDir'),
        Text('Cache Directory: $cacheDir'),
      ],
