        appBar: AppBar(
          title: Text('Animated Icon Example'),
        ),
        body: Center(
          child: AnimatedIcon(
            icon: AnimatedIcons.menu_arrow,
            progress: AlwaysStoppedAnimation(0.5), // Set the animation progress
            size: 100.0,
            color: Colors.blue,
          ),
        ),
      ),
